namespace ShopCart.Domain.Enities{    public class ShoppingCartItem    {        public string ProductId { get; set; } = string.Empty;        public string ProductName { get; set; } = string.Empty;        public decimal UnitPrice { get; set; }        public int Quantity { get; set; }        public ShoppingCartItem(string productId, string productName, decimal unitPrice, int quantity)        {            ProductId = productId;            ProductName = productName;            UnitPrice = unitPrice;            Quantity = quantity;        }        public void AddQuantity(int quantity) => Quantity += quantity;        public decimal TotalPrice => UnitPrice * Quantity;    }}